import React from 'react'
import {render} from 'react-dom'

const mystyle = {
	textAlign: 'center',
	fontSize: '20px'
}

class Container extends React.Component 
{
	render ()
	{
		var text = 'Class component';
		return 
		(
			<div>
				<p> { text } </p>
			</div>
		);
	}
}
/*class Container extends React.Component {
  render() {
    return (
