        <h1>I am the parent!</h1>
        <OurFirstComponent />
      </div>
    );
  }
}*/

function Hello ()
{
	return (
		<div>
			<p style={mystyle}> Hello world </p>
			{Getname()}
		</div>
	)
}
function Getname ()
{
	return (
		<div>
			<p style={mystyle}> Hello {prompt("Print name", "root")} </p>
		</div>
	)
}

render(<Hello />, document.getElementById("root"));
