import React from 'react'
import ReactDOM from 'react-dom';
import styles from './index.css';
import Menu from './Menu.js';
import MainContent from './MainContent';

import menu from './files/img/Menu.png';
import menu_border from './files/img/Menu_border.png';
import binany_logo from './files/img/binany_logo.png';
import down from './files/img/down.png';


class Main extends React.Component
{
	render ()
	{
		return (
			<div>
				<nav>
					<Menu />
				</nav>
				<MainContent />
			</div>
		)
	}
}
ReactDOM.render(<Main />, document.querySelector("main"));

class Head_content extends React.Component
{
	render ()
	{
		return (
			<div>
				<div className="header_left">
        			<div className="menu_button_container" id="menu_button">
        				<img src={menu} alt="menu" />
        			</div>
        			<img src={menu_border} alt="menu_border" />
        			<img src={binany_logo} alt="binany" />
      			</div>
      			<div className="header_right">
        			<div className="drop_menu">
          				<img src={menu_border} alt="menu_border" />
          				<p>Учебный счёт<br /><span>$20 000.00</span></p>
           				<img src={down} alt="down_drop" />
        			</div>
        			<img src={menu_border} alt="menu_border" />
        			<a id="replenish" href="#"><span>Пополнить счет</span></a>
      			</div>
      		</div>
		);
	}
}
ReactDOM.render(<Head_content />, document.querySelector("header"));

































window.onload = () => resizeMain ();
window.onresize = () => resizeMain ();

function resizeMain ()
{
	var height = window.innerHeight;
	document.querySelector("aside").style.height = height + 'px';
	height = height - 70;
	document.querySelector("main").style.height = height + 'px';
}
