import React from 'react'
import ReactDOM from 'react-dom';
import styles from './index.css';
import menu from './files/img/Menu.png';
import menu_border from './files/img/Menu_border.png';
import binany_logo from './files/img/binany_logo.png';
import down from './files/img/down.png';
import visa_mcard from '../files/img/visa_mcard.png'
import visa_mcard_color from '../files/img/visa_mcard_color.png'


class Main extends React.Component
{
	render ()
	{
		return (
			<div>
				<nav>
					<Menu />
				</nav>
				<MainContent />
			</div>


		)
	}
} ReactDOM.render(<Main />, document.querySelector("main"));


class MainContent extends React.Component
{
	render ()
	{
		return (
			<div>
				<div className="main_container">
        			<div className="main_content">
        				<p id="payment">Выберите способ оплаты</p>
        				<p className="main_content_text">Банковские карты</p>
        				<img src={visa_mcard} alt="visa & mcard" onmouseover={visa_mcard} onmouseout={visa_mcard_color} />
          				<p className="main_content_text">Электронные платежные системы</p>
          				<div className="cards_container">
            				<img src="img/qiwi.png" alt="qiwi_card" onmouseover="this.src=&quot;img/qiwi_color.png&quot;" onmouseout="this.src=&quot;img/qiwi.png&quot;" />
           					<img src="img/yandex.png" alt="yandex_card" onmouseover="this.src=&quot;img/yandex_color.png&quot;" onmouseout="this.src=&quot;img/yandex.png&quot;" />
            				<img src="img/webmoney.png" alt="webmoney_card" onmouseover="this.src=&quot;img/webmoney_color.png&quot;" onmouseout="this.src=&quot;img/webmoney.png&quot;" />
            				<img src="img/skrill.png" alt="skrill_card" onmouseover="this.src=&quot;img/skrill_color.png&quot;" onmouseout="this.src=&quot;img/skrill.png&quot;" />
        				</div>
					</div>
    			</div>
    		</div>
		)
	}
}

class Menu extends React.Component
{
	render ()
	{
		return (
				<div>
        			<div className="nav_left">
          				<p>Пополнить</p>
          				<p>Вывести</p>
          				<p>Операции</p>
          				<p>Сделки</p>
          				<p>Профиль</p>
          				<p>Котировки</p>
        			</div>
        		<div className="nav_right">
        			<p>Счет №: 31998640USD</p>
        		</div>
      		</div>
		)
	}
}

class Head_content extends React.Component
{
	render ()
	{
		return (
			<div>
				<div className="header_left">
        			<div className="menu_button_container" id="menu_button">
        				<img src={menu} alt="menu" />
        			</div>
        			<img src={menu_border} alt="menu_border" />
        			<img src={binany_logo} alt="binany" />
      			</div>
      			<div className="header_right">
        			<div className="drop_menu">
          				<img src={menu_border} alt="menu_border" />
          				<p>Учебный счёт<br /><span>$20 000.00</span></p>
           				<img src={down} alt="down_drop" />
        			</div>
        			<img src={menu_border} alt="menu_border" />
        			<a id="replenish" href="#"><span>Пополнить счет</span></a>
      			</div>
      		</div>
		);
	}
}
ReactDOM.render(<Head_content />, document.querySelector("header"));

































window.onload = () => resizeMain ();
window.onresize = () => resizeMain ();

function resizeMain ()
{
	var height = window.innerHeight;
	document.querySelector("aside").style.height = height + 'px';
	height = height - 70;
	document.querySelector("main").style.height = height + 'px';
}
