import React from 'react'
import {render} from 'react-dom'
import './index.css';

class Container extends React.Component 
{
	render ()
	{
		return (
			<div>
				<p> My component </p>
			</div>
		);
	}
}
class Status extends React.Component 
{
	constructor (props)
	{
		super(props);
		this.state = { st: true };
	}
	handleClick(event)
	{
		if (this.state.st) this.setState({ st: false });
		else this.setState({ st: true });
	}
	render ()
	{
		const stat = this.state.st ? true : false;
		if (stat) return ( <div><p className="statebut"> State is true</p></div>);
		else return (<div><p className="statebut"> State is false</p></div>);
	}
}
function Hello ()
{
	return (
		<div>
			<p> Hello world </p>
			{ Getname() }
			<Container />
			<Status />
		</div>
	)
}
function Getname ()
{
	return (
		<div>
			<p> Hello {prompt("Print name", "root")} </p>
		</div>
	)
}

render(<Hello />, document.getElementById("root"));
