import React from 'react'
import ReactDOM from 'react-dom';
import './index.css';

////// Функциональные и классовые компоненты //////
class WelcomCl extends React.Component
{
	render ()
	{
		return (<p>Hello {this.props.name}</p>);
	}
}

function Main ()
{
	return(
		<div>
			<WelcomCl name="root" />
			<Welcom name="world" />
			{Element[0]}
			{Element[1]}
		</div>
	);
}

function Welcom (props) // Тагже своиства можно передовать напрямую (без ключевого слова props) ({name})
{
	return (<p>Hello {props.name}</p>);
}

////// Отрисовка компонентов //////
var Element = new Array();

Element[0] = <p>Hello world</p>; // Простой React елемент

Element[1] = <Welcom name="root" />; // Елемент являющийся прототипом компонента Welcom

////// Рендеринг компонентов в DOM //////
ReactDOM.render(<Main />, document.querySelector("#root"));

////// Состаяния //////
 const INTERVAL = 100;

  class TimerCl extends React.Component {
    constructor(props) {
      super(props);
      this.state = {value: 0};
    }

    increment(){
      this.setState({value: this.state.value + 1});
    }

    componentDidMount() {
      this.timerID = setInterval(() => this.increment(), 1000/INTERVAL);
    }

    componentWillUnmount() {
      clearInterval(this.timerID);
    }

    render() {
      const value = this.state.value
      return (
        <div>
          <p>Таймер:</p>
          <p>
            <span>{Math.round(value/INTERVAL/60/60)} : </span>
            <span>{Math.round(value/INTERVAL/60)} : </span>
            <span>{Math.round(value/INTERVAL)} . </span>
            <span>{value % INTERVAL}</span>
          </p>
        </div>
      );
    }
  }
ReactDOM.render(<TimerCl/>, document.getElementById('timer'));



////// Таймер на основе функций //////
